import React from "react";
import "./App.css";

function TodoForm({ addTodo }) {
  const [value, setValue] = React.useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!value) return;
    addTodo(value, new Date().toLocaleString());
    setValue("");
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        className="input"
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
    </form>
  );
}
function Todo({ todo, index, removeTodo }) {
  return (
    <div className="col todo">
      <span className="displayspan">{`${todo.text} ${todo.date}`}</span>
      <div>
        <button
          className="delete btn btn-outline-danger btn-sm"
          onClick={() => removeTodo(index)}
        >
          x
        </button>
      </div>
    </div>
  );
}

function App() {
  const [todos, setTodos] = React.useState([
    { text: "Note Party!", date: "01/01/1999, 0:00:01 AM" },
  ]);
  const removeTodo = (index) => {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  };

  const addTodo = (text, date) => {
    const newTodos = [...todos, { text }];
    newTodos.slice(-1)[0].date = date;
    setTodos(newTodos);
  };

  return (
    <div className="row app">
      <h1>Please Leave a Note!</h1>
      <div className="bigForm">
        <TodoForm addTodo={addTodo} />
        {todos.map((todo, index) => (
          <Todo key={index} index={index} todo={todo} removeTodo={removeTodo} />
        ))}
      </div>
    </div>
  );
}

export default App;
